// Parse the AI response and extract JSON
const response = $input.first().json.choices[0].message.content;

// Try to extract JSON from the response
let blueprint;
try {
  // Remove markdown code blocks if present
  let jsonStr = response;
  if (response.includes('```json')) {
    jsonStr = response.split('```json')[1].split('```')[0];
  } else if (response.includes('```')) {
    jsonStr = response.split('```')[1].split('```')[0];
  }
  blueprint = JSON.parse(jsonStr.trim());
} catch (e) {
  throw new Error('Failed to parse blueprint JSON: ' + e.message);
}

// Get the previous state
const prevState = $('ğŸ”§ Initialize BookState').first().json;

return {
  json: {
    ...prevState,
    blueprint: blueprint,
    status: 'blueprint_ready'
  }
};

// Get current state
const state = $('ğŸ“‹ Parse Blueprint').first().json;
const chapters = state.blueprint.chapters;

// Return chapters as array for loop
return chapters.map((chapter, index) => ({
  json: {
    ...state,
    current_chapter: chapter,
    current_chapter_index: index
  }
}));

// Reset revision counter for this chapter (using workflow static data)
const staticData = $getWorkflowStaticData('global');
staticData.revision_count = 0;

// Get chapter data
const chapterLoop = $('ğŸ” Chapter Loop').first().json;
const researchNotes = $input.first().json.choices[0].message.content;

return {
  json: {
    book_id: chapterLoop.book_id,
    current_chapter: chapterLoop.current_chapter,
    current_chapter_index: chapterLoop.current_chapter_index,
    research_notes: researchNotes,
    blueprint: chapterLoop.blueprint,
    target_audience: chapterLoop.target_audience,
    revision_count: 0
  }
};

const draft = $input.first().json.choices[0].message.content;

// Get base data from Store Research (always available)
let prevData = $('ğŸ’¾ Store Research').first().json;

// Get revision count from workflow static data (persists across loop iterations)
const staticData = $getWorkflowStaticData('global');
let revision_count = staticData.revision_count || 0;

// Extract CODE_REQUEST placeholders
const codeRequests = [];
const regex = /<<CODE_REQUEST:\s*(.+?)>>/g;
let match;

while ((match = regex.exec(draft)) !== null) {
  codeRequests.push({
    placeholder: match[0],
    description: match[1].trim()
  });
}

return {
  json: {
    ...prevData,
    draft_content: draft,
    code_requests: codeRequests,
    has_code_requests: codeRequests.length > 0,
    revision_count: revision_count
  }
};

const prevData = $('ğŸ“ Extract Code Requests').first().json;
const codeResponse = $input.first().json.choices[0].message?.content || '';

// Replace placeholders with actual code
let finalDraft = prevData.draft_content;

// Simple replacement - in production, use more sophisticated matching
prevData.code_requests.forEach((req, index) => {
  // Find the corresponding code block in the response
  const codeBlocks = codeResponse.match(/```[\s\S]*?```/g) || [];
  if (codeBlocks[index]) {
    finalDraft = finalDraft.replace(req.placeholder, codeBlocks[index]);
  }
});

return {
  json: {
    ...prevData,
    draft_content: finalDraft,
    code_validated: true
  }
};

const response = $input.first().json.choices[0].message.content;

// Get previous data - try Merge Code first (if code was needed), otherwise Skip Code or Extract Code Requests
let prevData;
try {
  prevData = $('ğŸ”— Merge Code').first().json;
} catch (e1) {
  try {
    prevData = $('â­ï¸ Skip Code').first().json;
  } catch (e2) {
    prevData = $('ğŸ“ Extract Code Requests').first().json;
  }
}

// Parse editor response
let editorResult;
try {
  let jsonStr = response;
  if (response.includes('```json')) {
    jsonStr = response.split('```json')[1].split('```')[0];
  } else if (response.includes('```')) {
    jsonStr = response.split('```')[1].split('```')[0];
  }
  editorResult = JSON.parse(jsonStr.trim());
} catch (e) {
  editorResult = {
    score: 85,
    approved: true,
    feedback: { strengths: [], improvements: [] },
    exam_questions: []
  };
}

// Increment revision count using workflow static data (persists across loop)
const staticData = $getWorkflowStaticData('global');
staticData.revision_count = (staticData.revision_count || 0) + 1;
const revision_count = staticData.revision_count;

return {
  json: {
    ...prevData,
    editor_score: editorResult.score,
    editor_feedback: editorResult.feedback,
    editor_approved: editorResult.approved,
    exam_questions: editorResult.exam_questions,
    revision_count: revision_count
  }
};

// Collect all completed chapters
const chapters = $input.all().map(item => ({
  chapter_number: item.json.current_chapter.number,
  title: item.json.current_chapter.title,
  content: item.json.draft_content,
  exam_questions: item.json.exam_questions,
  score: item.json.editor_score
}));

// Sort by chapter number
chapters.sort((a, b) => a.chapter_number - b.chapter_number);

// Create final book markdown
let bookMarkdown = `# ${$input.first().json.blueprint.title}\n\n`;
bookMarkdown += `*${$input.first().json.blueprint.subtitle || ''}*\n\n`;
bookMarkdown += `---\n\n`;

chapters.forEach(ch => {
  bookMarkdown += ch.content + '\n\n---\n\n';
});

// Collect all exam questions
const allQuestions = chapters.flatMap(ch => 
  ch.exam_questions.map(q => ({
    ...q,
    chapter: ch.chapter_number
  }))
);

return {
  json: {
    book_id: $input.first().json.book_id,
    title: $input.first().json.blueprint.title,
    final_markdown: bookMarkdown,
    exam_questions: allQuestions,
    chapter_scores: chapters.map(ch => ({ chapter: ch.chapter_number, score: ch.score })),
    average_score: chapters.reduce((sum, ch) => sum + ch.score, 0) / chapters.length,
    completed_at: new Date().toISOString()
  }
};
