// R2.1.4 + R2.1.5: Merge closer into chapter JSON and return.
// The accumulator IS the output ‚Äî no text-to-JSON conversion needed.
const staticData = $getWorkflowStaticData('global');
const initData = $('üìù Init Accumulator').first().json;
const response = $input.first().json.choices[0].message.content;

// Parse closer
let closer;
try {
  let jsonStr = response;
  if (response.includes('```json')) {
    jsonStr = response.split('```json')[1].split('```')[0];
  } else if (response.includes('```')) {
    jsonStr = response.split('```')[1].split('```')[0];
  }
  closer = JSON.parse(jsonStr.trim());
} catch (e) {
  closer = {
    synthesis: response,
    key_takeaways: [],
    mcqs: [],
    drill: { description: 'See chapter content for exercise' }
  };
}

// Merge closer into the chapter JSON accumulator (summary + assessment keys)
const chapterJson = staticData.current_chapter_json;
chapterJson.chapter_id = initData.chapter_id;
chapterJson.title = initData.title;
chapterJson.domain_id = initData.domain_id;
chapterJson.summary = closer.synthesis || closer.summary || '';
chapterJson.assessment = {
  scenario_resolution: closer.scenario_resolution || '',
  key_takeaways: closer.key_takeaways || [],
  mcqs: closer.mcqs || [],
  drill: closer.drill || {}
};

const codeRequests = staticData.code_requests || [];

// Build chapter summary for global_history
const summary = 'Kapitel "' + initData.title + '" (Domain: ' + initData.domain_id + '): ' +
  (initData.learning_objectives || []).map(function(lo) { return lo.description; }).join('; ') +
  '. ' + (chapterJson.summary || '').substring(0, 200);

// Clean up static data
delete staticData.current_chapter_json;
delete staticData.code_requests;

return [{
  json: {
    status: 'success',
    json_content: chapterJson,
    chapter_summary: summary,
    code_requests: codeRequests,
    has_code_requests: codeRequests.length > 0,
    job_id: initData.job_id
  }
}];

// R2.2.2 + R2.2.3: Validate chapter JSON against schema before returning
const input = $input.first().json;
const chapter = input.json_content;

function validateChapterSchema(ch) {
  const errors = [];
  const required = ['chapter_id', 'title', 'metadata', 'sections', 'summary', 'assessment'];
  for (const key of required) {
    if (ch[key] === undefined || ch[key] === null) {
      errors.push('Missing required key: ' + key);
    }
  }
  if (typeof ch.chapter_id !== 'string') errors.push('chapter_id must be a string');
  if (typeof ch.title !== 'string' || !ch.title) errors.push('title must be a non-empty string');
  if (typeof ch.summary !== 'string' || !ch.summary) errors.push('summary must be a non-empty string');
  // metadata
  if (ch.metadata && typeof ch.metadata === 'object') {
    if (!ch.metadata.header) errors.push('Missing metadata.header');
    if (!ch.metadata.learning_objectives) errors.push('Missing metadata.learning_objectives');
    else if (!Array.isArray(ch.metadata.learning_objectives)) errors.push('metadata.learning_objectives must be an array');
    if (!ch.metadata.professional_context) errors.push('Missing metadata.professional_context');
  }
  // sections
  if (Array.isArray(ch.sections)) {
    if (ch.sections.length === 0) errors.push('sections must be a non-empty array');
    ch.sections.forEach(function(s, i) {
      if (!s.lo_id) errors.push('sections[' + i + '] missing lo_id');
      if (!s.theory_content) errors.push('sections[' + i + '] missing theory_content');
      else if (!Array.isArray(s.theory_content)) errors.push('sections[' + i + '].theory_content must be an array');
      if (!s.key_takeaway) errors.push('sections[' + i + '] missing key_takeaway');
    });
  } else if (ch.sections !== undefined) {
    errors.push('sections must be an array');
  }
  // assessment
  if (ch.assessment && typeof ch.assessment === 'object') {
    if (!Array.isArray(ch.assessment.mcqs)) errors.push('assessment.mcqs must be an array');
    else if (ch.assessment.mcqs.length === 0) errors.push('assessment.mcqs must not be empty');
    if (!ch.assessment.drill || typeof ch.assessment.drill !== 'object') errors.push('assessment.drill must be an object');
  }
  return { valid: errors.length === 0, errors: errors };
}

const result = validateChapterSchema(chapter);

if (result.valid) {
  return [{ json: input }];
} else {
  return [{ json: {
    status: 'failed',
    validation_errors: result.errors,
    json_content: chapter,
    chapter_summary: input.chapter_summary || '',
    code_requests: input.code_requests || [],
    has_code_requests: input.has_code_requests || false,
    job_id: input.job_id
  }}];
}
