parameters.jsCode:
// Reset revision counter for this chapter
const staticData = $getWorkflowStaticData('global');
staticData.revision_count = 0;

// Get all input items - merged from: MCP Research, MCP Syllabus, Chapter Loop
const allItems = $input.all();

// Find chapter data, research results, and syllabus LOs from merged inputs
let chapterResearch = [];
let syllabusLOs = [];
let chapterData = null;

for (const item of allItems) {
  const json = item.json;

  // Check if this is chapter data from the loop (has current_chapter)
  if (json.current_chapter && json.blueprint) {
    chapterData = json;
    continue;
  }

  // Check if this is an MCP HTTP response (has body.content structure)
  if (json.body?.content?.[0]?.text) {
    try {
      const parsed = JSON.parse(json.body.content[0].text);

      // Check for research results (has results_count or query field)
      if (parsed.query !== undefined || parsed.results_count !== undefined) {
        chapterResearch = parsed.results || [];
      }
      // Check for syllabus section data
      else if (parsed.results && parsed.count !== undefined) {
        syllabusLOs = parsed.results || [];
      }
      else if (parsed.section || parsed.found || parsed.learning_objectives) {
        syllabusLOs = parsed.learning_objectives || parsed.results || [];
        if (parsed.section?.topics) {
          for (const topic of parsed.section.topics) {
            if (topic.learning_objectives) {
              syllabusLOs = syllabusLOs.concat(topic.learning_objectives);
            }
          }
        }
      }
    } catch (e) {
      console.log('Failed to parse MCP response:', e.message);
    }
  }
}

// Verify we have chapter data
if (!chapterData) {
  throw new Error('Chapter data not found in merged inputs. Check workflow connections.');
}

// Merge original LOs from domain with fetched LOs
const allLOs = [...(chapterData.current_chapter.original_learning_objectives || []), ...syllabusLOs];

return {
  json: {
    book_id: chapterData.book_id,
    current_chapter: chapterData.current_chapter,
    current_chapter_index: chapterData.current_chapter_index,
    chapter_research: chapterResearch,
    syllabus_learning_objectives: allLOs,
    blueprint: chapterData.blueprint,
    target_audience: chapterData.target_audience,
    revision_count: 0
  }
};

const draft = $input.first().json.choices[0].message.content;

// Get base data from Merge Chapter Context
let prevData = $('üíæ Merge Chapter Context').first().json;

// Get revision count from workflow static data
const staticData = $getWorkflowStaticData('global');
let revision_count = staticData.revision_count || 0;

// Extract CODE_REQUEST placeholders
const codeRequests = [];
const regex = /<<CODE_REQUEST:\s*(.+?)>>/g;
let match;

while ((match = regex.exec(draft)) !== null) {
  codeRequests.push({
    placeholder: match[0],
    description: match[1].trim()
  });
}

return {
  json: {
    ...prevData,
    draft_content: draft,
    code_requests: codeRequests,
    has_code_requests: codeRequests.length > 0,
    revision_count: revision_count
  }
};

const prevData = $('üìù Extract Code Requests').first().json;
const codeResponse = $input.first().json.choices[0].message?.content || '';

// Extract code blocks for validation
const codeBlocks = codeResponse.match(/```([\w]*)\n([\s\S]*?)```/g) || [];
const extractedCode = codeBlocks.map(block => {
  const match = block.match(/```([\w]*)\n([\s\S]*?)```/);
  return {
    language: match[1] || 'javascript',
    code: match[2].trim()
  };
});

// Replace placeholders with actual code
let finalDraft = prevData.draft_content;
prevData.code_requests.forEach((req, index) => {
  if (codeBlocks[index]) {
    finalDraft = finalDraft.replace(req.placeholder, codeBlocks[index]);
  }
});

return {
  json: {
    ...prevData,
    draft_content: finalDraft,
    extracted_code: extractedCode,
    raw_code_response: codeResponse,
    code_validated: false
  }
};

