const prevData = $('üîó Merge Code').first().json;

// Parse validation result from MCP-Coder
let validationResult = { valid: true, errors: [], suggestions: [] };
try {
  const valResp = $('üî¨ MCP: Validate Code').first().json;
  if (valResp.body?.content?.[0]?.text) {
    validationResult = JSON.parse(valResp.body.content[0].text);
  }
} catch (e) {
  console.log('Code validation not available:', e.message);
}

// Get static data for retry count
const staticData = $getWorkflowStaticData('global');
staticData.code_retry_count = staticData.code_retry_count || 0;

return {
  json: {
    ...prevData,
    code_validation: validationResult,
    code_validated: validationResult.valid,
    code_errors: validationResult.errors || [],
    code_suggestions: validationResult.suggestions || [],
    code_retry_count: staticData.code_retry_count
  }
};

// Increment retry counter and merge corrected code
const staticData = $getWorkflowStaticData('global');
staticData.code_retry_count = (staticData.code_retry_count || 0) + 1;

const prevData = $('üìä Parse Code Validation').first().json;
const codeResponse = $input.first().json.choices[0].message?.content || '';

// Extract code blocks for validation
const codeBlocks = codeResponse.match(/```([\w]*)\n([\s\S]*?)```/g) || [];
const extractedCode = codeBlocks.map(block => {
  const match = block.match(/```([\w]*)\n([\s\S]*?)```/);
  return {
    language: match[1] || 'javascript',
    code: match[2].trim()
  };
});

// Re-merge into draft
let finalDraft = prevData.draft_content;
const originalCodeBlocks = prevData.draft_content.match(/```[\s\S]*?```/g) || [];
originalCodeBlocks.forEach((oldBlock, index) => {
  if (codeBlocks[index]) {
    finalDraft = finalDraft.replace(oldBlock, codeBlocks[index]);
  }
});

return {
  json: {
    ...prevData,
    draft_content: finalDraft,
    extracted_code: extractedCode,
    raw_code_response: codeResponse,
    code_validated: false,
    code_retry_count: staticData.code_retry_count
  }
};

