const response = $input.first().json.choices[0].message.content;

// Get previous data
let prevData;
try {
  prevData = $('üîó Merge Code').first().json;
} catch (e1) {
  try {
    prevData = $('‚è≠Ô∏è Skip Code').first().json;
  } catch (e2) {
    prevData = $('üìù Extract Code Requests').first().json;
  }
}

// Parse WPI ISO Editor response
let editorResult;
try {
  let jsonStr = response;
  if (response.includes('```json')) {
    jsonStr = response.split('```json')[1].split('```')[0];
  } else if (response.includes('```')) {
    jsonStr = response.split('```')[1].split('```')[0];
  }
  editorResult = JSON.parse(jsonStr.trim());
} catch (e) {
  editorResult = {
    score: 0,
    approved: false,
    iso_compliant: false,
    audit_log: { satisfied_LOs: [], missing_LOs: [], tone_violations: [], undefined_acronyms: [], code_blocks_count: 0, code_validation_passed: false },
    scoring_breakdown: { lo_completeness: 0, clarity_and_tone: 0, code_quality: 0, didactic_structure: 0, exercises: 0 },
    feedback: { strengths: [], required_changes: ['Failed to parse ISO Editor response: ' + e.message] },
    exam_questions: []
  };
}

// APPROVAL THRESHOLD: >= 90 points
const APPROVAL_THRESHOLD = 90;
const score = editorResult.score || 0;
const isApproved = score >= APPROVAL_THRESHOLD;
editorResult.approved = isApproved;

// Increment revision count
const staticData = $getWorkflowStaticData('global');
staticData.revision_count = (staticData.revision_count || 0) + 1;
const revision_count = staticData.revision_count;

return {
  json: {
    ...prevData,
    editor_score: score,
    editor_approved: isApproved,
    approval_threshold: APPROVAL_THRESHOLD,
    iso_compliant: editorResult.iso_compliant || false,
