parameters.jsCode:
// Mark chapter as stored and output complete chapter data
let prevData;
try {
  prevData = $('üìä Parse ISO Editor Result').first().json;
} catch (e) {
  prevData = $input.first().json;
}

// Try to get ingestion result
let ingestionResult = null;
try {
  const ingestResp = $('üíæ MCP: Store in Knowledge Base').first().json;
  if (ingestResp.body?.content?.[0]?.text) {
    ingestionResult = JSON.parse(ingestResp.body.content[0].text);
  }
} catch (e) {
  console.log('Ingestion result not available:', e.message);
}

const chapterNumber = prevData.current_chapter?.number || (prevData.current_chapter_index !== undefined ? prevData.current_chapter_index + 1 : 1);

// Format exam questions as Markdown
const examQuestionsMarkdown = (prevData.exam_questions || []).map((q, i) => {
  return '**Frage ' + (i + 1) + ':** ' + q.question + '\n\n' +
    (q.options || []).join('\n') + '\n\n' +
    '> **Antwort:** ' + (q.correct || 'N/A') + '\n> *' + (q.explanation || '') + '*';
}).join('\n\n---\n\n');

console.log('Finalizing chapter', chapterNumber, 'score:', prevData.editor_score || 0);

return {
  json: {
    chapter_number: chapterNumber,
    title: prevData.current_chapter?.title || 'Untitled',
    content: prevData.draft_content || '',
    exam_questions: prevData.exam_questions || [],
    exam_questions_markdown: examQuestionsMarkdown,
    score: prevData.editor_score || 0,
    iso_compliant: prevData.iso_compliant || false,
    approval_threshold: prevData.approval_threshold || 90,
    passed_quality: prevData.editor_approved || false,
    stored_in_kb: !!ingestionResult,
    kb_document_id: ingestionResult?.document_id || null,
    blueprint: prevData.blueprint,
    book_id: prevData.book_id,
    current_chapter_index: prevData.current_chapter_index,
    target_audience: prevData.target_audience
  }
};

// Get chapters from the accumulator service
console.log('=== COMPILE BOOK DEBUG ===');

let finalChapters = [];
let book_id = 'unknown';
let blueprint = { title: 'Unknown Book', subtitle: '' };

try {
  const accumulatorResponse = $('üì• Get Accumulated Chapters').first().json;
  console.log('Accumulator response chapters:', accumulatorResponse.chapters?.length || 0);

  if (accumulatorResponse.chapters && Array.isArray(accumulatorResponse.chapters)) {
    finalChapters = accumulatorResponse.chapters;
    book_id = accumulatorResponse.book_id || 'unknown';
  }

  if (finalChapters.length > 0 && finalChapters[0].blueprint) {
    blueprint = finalChapters[0].blueprint;
  }
} catch (e) {
  console.log('Could not get chapters from accumulator:', e.message);
}

console.log('Final chapters to compile:', finalChapters.length);

if (finalChapters.length === 0) {
  return {
    json: {
      book_id: book_id,
      title: 'ERROR: No chapters',
      error: 'No chapters were accumulated',
      final_markdown: '# ERROR\n\nNo chapters were processed.',
      exam_questions_markdown: '# Keine Pr√ºfungsfragen\n\nKeine Kapitel wurden verarbeitet.',
      chapter_scores: [],
      average_score: 0,
      total_chapters: 0,
      completed_at: new Date().toISOString()
    }
  };
}

// Sort by chapter number
finalChapters.sort((a, b) => (a.chapter_number || 0) - (b.chapter_number || 0));

// Create final book markdown
let bookMarkdown = '# ' + blueprint.title + '\n\n';
bookMarkdown += '*' + (blueprint.subtitle || '') + '*\n\n';

if (blueprint.iso_alignment) {
  bookMarkdown += '**ISO 17024 Syllabus:** ' + (blueprint.iso_alignment.syllabus_id || blueprint.iso_alignment.domain_id) + '\n\n';
}

bookMarkdown += '---\n\n## Inhaltsverzeichnis\n\n';
finalChapters.forEach((ch, i) => {
  bookMarkdown += (i + 1) + '. ' + (ch.title || 'Kapitel ' + (i + 1)) + '\n';
});
bookMarkdown += '\n---\n\n';

finalChapters.forEach((ch, i) => {
  const chapterContent = ch.content || '';
  console.log('Chapter', ch.chapter_number || i + 1, 'content length:', chapterContent.length);
  if (chapterContent) {
    bookMarkdown += chapterContent + '\n\n---\n\n';
  }
});

// Collect all exam questions as Markdown
let allQuestionsMarkdown = '# Pr√ºfungsfragen\n\n';
finalChapters.forEach((ch) => {
  if (ch.exam_questions_markdown) {
    allQuestionsMarkdown += '## Kapitel ' + ch.chapter_number + ': ' + ch.title + '\n\n';
    allQuestionsMarkdown += ch.exam_questions_markdown + '\n\n---\n\n';
  }
});

// Calculate average score
const validScores = finalChapters.filter(ch => typeof ch.score === 'number' && ch.score > 0);
const averageScore = validScores.length > 0 
  ? validScores.reduce((sum, ch) => sum + ch.score, 0) / validScores.length 
  : 0;

console.log('Average score:', averageScore);
console.log('=== END DEBUG ===');

return {
  json: {
    book_id: book_id,
    title: blueprint.title,
    iso_alignment: blueprint.iso_alignment,
    final_markdown: bookMarkdown,
    exam_questions_markdown: allQuestionsMarkdown,
    chapter_scores: finalChapters.map(ch => ({ 
      chapter: ch.chapter_number || 0, 
      title: ch.title,
      score: ch.score || 0,
      iso_compliant: ch.iso_compliant || false,
      passed_quality: ch.passed_quality || false
    })),
    average_score: averageScore,
    kb_document_ids: finalChapters.map(ch => ch.kb_document_id).filter(Boolean),
    total_chapters: finalChapters.length,
    completed_at: new Date().toISOString()
  }
};

