// Mark chapter as stored and continue to loop
const prevData = $('ğŸ“Š Parse Editor Result').first().json;

// Try to get ingestion result
let ingestionResult = null;
try {
  const ingestResp = $('ğŸ’¾ MCP: Store in Knowledge Base').first().json;
  if (ingestResp.body?.content?.[0]?.text) {
    ingestionResult = JSON.parse(ingestResp.body.content[0].text);
  }
} catch (e) {
  console.log('Ingestion result not available:', e.message);
}

return {
  json: {
    ...prevData,
    stored_in_kb: !!ingestionResult,
    kb_document_id: ingestionResult?.document_id || null
  }
};

// Collect all completed chapters
const chapters = $input.all().map(item => ({
  chapter_number: item.json.current_chapter.number,
  title: item.json.current_chapter.title,
  content: item.json.draft_content,
  exam_questions: item.json.exam_questions || [],
  score: item.json.editor_score,
  iso_compliant: item.json.iso_compliant,
  kb_document_id: item.json.kb_document_id
}));

// Sort by chapter number
chapters.sort((a, b) => a.chapter_number - b.chapter_number);

// Create final book markdown
const blueprint = $input.first().json.blueprint;
let bookMarkdown = '# ' + blueprint.title + '\n\n';
bookMarkdown += '*' + (blueprint.subtitle || '') + '*\n\n';

if (blueprint.iso_alignment) {
  bookMarkdown += '**ISO 17024 Domain:** ' + blueprint.iso_alignment.domain_id + '\n\n';
}

bookMarkdown += '---\n\n';

chapters.forEach(ch => {
  bookMarkdown += ch.content + '\n\n---\n\n';
});

// Collect all exam questions
const allQuestions = chapters.flatMap(ch => 
  (ch.exam_questions || []).map(q => ({
    ...q,
    chapter: ch.chapter_number
  }))
);

return {
  json: {
    book_id: $input.first().json.book_id,
    title: blueprint.title,
    iso_alignment: blueprint.iso_alignment,
    final_markdown: bookMarkdown,
    exam_questions: allQuestions,
    chapter_scores: chapters.map(ch => ({ 
      chapter: ch.chapter_number, 
      score: ch.score,
      iso_compliant: ch.iso_compliant 
    })),
    average_score: chapters.reduce((sum, ch) => sum + ch.score, 0) / chapters.length,
    kb_document_ids: chapters.map(ch => ch.kb_document_id).filter(Boolean),
    completed_at: new Date().toISOString()
  }
};
