// Merge chapter data with research results
const chapterData = $('ğŸ” Chapter Loop').first().json;

// Parse research results
let researchContext = [];
try {
  const researchResp = $('ğŸ” MCP: Chapter Research').first().json;
  if (researchResp.body?.content?.[0]?.text) {
    const parsed = JSON.parse(researchResp.body.content[0].text);
    researchContext = parsed.results || [];
  }
} catch (e) {
  console.log('Research not available:', e.message);
}

return {
  json: {
    ...chapterData,
    research_context: researchContext
  }
};

// Parse writer response and extract chapter + questions
const prevData = $('ğŸ’¾ Merge Research').first().json;
const response = $input.first().json.choices[0].message.content;

// Extract exam questions section
let chapterContent = response;
let examQuestions = [];

// Try to extract questions from the markdown
const questionsMatch = response.match(/##\s*Pruefungsfragen[\s\S]*$/i) || response.match(/##\s*PrÃ¼fungsfragen[\s\S]*$/i);
if (questionsMatch) {
  const questionsSection = questionsMatch[0];
  // Parse individual questions
  const questionBlocks = questionsSection.split(/###\s*Frage\s*\d+/i).slice(1);
  examQuestions = questionBlocks.map((block, i) => {
    const lines = block.trim().split('\n').filter(l => l.trim());
    return {
      question_number: i + 1,
      text: lines[0] || '',
      raw_content: block.trim()
    };
  });
}

// Calculate word count and workload
const wordCount = response.split(/\s+/).length;
const workloadMinutes = Math.ceil(wordCount / 40 / 10) * 10;

return {
  json: {
    book_id: prevData.book_id,
    syllabus_name: prevData.syllabus_name,
    target_audience: prevData.target_audience,
    total_chapters: prevData.total_chapters,
    current_chapter_index: prevData.current_chapter_index,
    chapter_number: prevData.chapter.chapter_number,
    chapter_title: prevData.chapter.title,
    chapter_domain_id: prevData.chapter.domain_id,
    chapter_content_md: response,
    exam_questions: examQuestions,
    exam_questions_count: examQuestions.length,
    word_count: wordCount,
    workload_minutes: workloadMinutes
  }
};

// Compile all chapters into final book
const response = $input.first().json;
const chapters = response.chapters || [];

if (chapters.length === 0) {
  return {
    json: {
      error: 'No chapters found',
      book_id: response.book_id
    }
  };
}

// Get book metadata from first chapter
const firstChapter = chapters[0];
const syllabus_name = firstChapter.syllabus_name || 'Unknown';
const target_audience = firstChapter.target_audience || 'Unknown';
const book_id = firstChapter.book_id || response.book_id;

// Sort chapters by number
chapters.sort((a, b) => (a.chapter_number || 0) - (b.chapter_number || 0));

// Build full book markdown
let bookContent = '# ' + syllabus_name + '\n\n';
bookContent += '**Zielgruppe:** ' + target_audience + '\n\n';
bookContent += '**Generiert am:** ' + new Date().toLocaleDateString('de-DE') + '\n\n';
bookContent += '---\n\n';

// Table of contents
bookContent += '## Inhaltsverzeichnis\n\n';
chapters.forEach(ch => {
  bookContent += (ch.chapter_number || '?') + '. ' + (ch.chapter_title || 'Untitled') + '\n';
});
bookContent += '\n---\n\n';

// Add all chapters
chapters.forEach(ch => {
  bookContent += (ch.chapter_content_md || '') + '\n\n---\n\n';
});

// Compile all exam questions
bookContent += '# Pruefungsfragen - Gesamtuebersicht\n\n';
chapters.forEach(ch => {
  if (ch.exam_questions && ch.exam_questions.length > 0) {
    bookContent += '## Kapitel ' + ch.chapter_number + ': ' + ch.chapter_title + '\n\n';
    ch.exam_questions.forEach(q => {
      bookContent += q.raw_content + '\n\n';
    });
  }
});

// Statistics
const totalWords = chapters.reduce((sum, ch) => sum + (ch.word_count || 0), 0);
const totalQuestions = chapters.reduce((sum, ch) => sum + (ch.exam_questions_count || 0), 0);
const totalWorkload = chapters.reduce((sum, ch) => sum + (ch.workload_minutes || 0), 0);

return {
  json: {
    book_id: book_id,
    title: syllabus_name,
    target_audience: target_audience,
    book_content_md: bookContent,
    total_chapters: chapters.length,
    total_words: totalWords,
    total_questions: totalQuestions,
    total_workload_minutes: totalWorkload,
    completed_at: new Date().toISOString()
  }
};

